      { id: 9, name:"許昌", human:0, team:0},
      { id: 10, name:"洛陽", human:0, team:0},
      { id: 11, name:"長安", human:0, team:0},
      { id: 12, name:"天水", human:0, team:0},
      { id: 13, name:"漢中", human:0, team:0},
      { id: 14, name:"成都", human:0, team:0},
      { id: 15, name:"江州", human:0, team:0},
      { id: 16, name:"襄陽", human:0, team:0},
      { id: 17, name:"長沙", human:0, team:0},
      { id: 18, name:"盧江", human:0, team:0},
      { id: 19, name:"小沛", human:0, team:0},
      { id: 20, name:"建業", human:0, team:0},
      { id: 21, name:"呉郡", human:0, team:0},
    ]
    }
  },
  computed:{
    multi() {
      return (Math.round(Math.round((this.input1 * this.minutes + this.input2) / this.second) / 2) * 2 * this.input3)
    },
    multi2() {
      return (Math.round(Math.round((this.input1 * this.minutes + this.input2) / this.second) / 2) * 2)
    },
    totalteam() {
      let total = 0
      const length = this.gunnyuuData.length
      for(let i = 0; i < length; i++) {
        total += this.gunnyuuData[i].team
      }
      return total 
    },
    totalhuman() {
      let total = 0
      const length = this.gunnyuuData.length
      for(let i = 0; i < length; i++) {
        total += this.gunnyuuData[i].human
      }
      return total 
    },
  },
  methods: {
    show(nu) {
      this.num = this.gunnyuuData[nu]
      this.$modal.show('hello-world');
    },
    hide() {
      this.num.human = this.multi
      this.num.team = this.multi2
      this.$modal.hide('hello-world');
    },
    resethuman() {
      this.gunnyuuData.forEach(element => {
        element.human = 0
      });
    }
  }
}
</script>

